INSERT INTO `ElementContent` (`ID`, `HTML`, `MenuTitle`) VALUES
(8, '<div class=\"section-description\">\n<div>A collection of curated Bitcoin resources.</div>\n</div><div class=\"resources-list\">\n<h4 class=\"resources-subheading\">Knowledge &amp; Discussion</h4>\n<div class=\"resources-item\"><a class=\"resources-item-link\" rel=\"noopener noreferrer\" href=\"https://stacker.news/\" target=\"_blank\">stacker.news</a>\n<div class=\"resources-item-description\">A Bitcoin discussion board like Hacker News, but where you can tip with Sats.</div>\n</div>\n<div class=\"resources-item\"><a class=\"resources-item-link\" rel=\"noopener noreferrer\" href=\"https://www.lopp.net/\" target=\"_blank\">Lopp.net</a>\n<div class=\"resources-item-description\">Jameson Lopp is a long time bitcoiner, and a prolific educator in the space. Lopp.net has some of the best information for new users anywhere.</div>\n</div>\n<div class=\"resources-item\"><a class=\"resources-item-link\" rel=\"noopener noreferrer\" href=\"https://bitcoiner.guide/\" target=\"_blank\">Bitcoiner.guide</a>\n<div class=\"resources-item-description\">BitcoinQ&amp;A is well known in bitcoin for his bitcoin.guide and contributing to various projects in bitcoin</div>\n</div>\n<h4 class=\"resources-subheading\">Podcasting 2.0</h4>\n<div class=\"resources-item\"><a class=\"resources-item-link\" rel=\"noopener noreferrer\" href=\"https://fountain.fm/show/yeyKUxKjAw151iZAQhcw\" target=\"_blank\">The Transformation of Value - A bitcoin Podcast by Cody Ellingham</a>\n<div class=\"resources-item-description\">An NZ-based Bitcoin Podcast.</div>\n</div>\n<div class=\"resources-item\"><a class=\"resources-item-link\" rel=\"noopener noreferrer\" href=\"https://www.swanbitcoin.com/signal/\" target=\"_blank\">Swan Signal</a>\n<div class=\"resources-item-description\">Swan is a bitcoin only exchange, and has a mission focused on self custody and education</div>\n</div>\n<div class=\"resources-item\"><a class=\"resources-item-link\" href=\"https://podcasts.apple.com/us/podcast/tftc-a-bitcoin-podcast/id1292381204\">TFTC: A Bitcoin Podcast</a>\n<div class=\"resources-item-description\">TFTC is a podcast hosted by Marty Bent about Bitcoin. Join Marty, Editor in Chief of \"the best newsletter in Bitcoin\", as he sits down to discuss Bitcoin with interesting people.</div>\n</div>\n<div class=\"resources-item\"><a class=\"resources-item-link\" rel=\"noopener noreferrer\" href=\"https://value4value.info/\" target=\"_blank\">value4value</a>\n<div class=\"resources-item-description\">value4value is a concept for how creators can monetise their content, writing, and podcasts via the Lightning Network over the likes of fountain.fm.</div>\n</div>\n<div class=\"resources-item\"><a class=\"resources-item-link\" rel=\"noopener noreferrer\" href=\"https://fountain.fm/\" target=\"_blank\">fountain.fm</a>\n<div class=\"resources-item-description\">Listen, learn, and earn for the value you bring to the platform by streaming sats over the Lightning Nework.</div>\n</div>\n<h4 class=\"resources-subheading\">Get Setup With a Wallet</h4>\n<div class=\"resources-item\"><a class=\"resources-item-link\" rel=\"noopener noreferrer\" href=\"https://www.walletofsatoshi.com/\" target=\"_blank\">Wallet of Satoshi</a>\n<div class=\"resources-item-description\">The easiest Bitcoin wallet to get up and running with, has Lightning support. iOS &amp; Android.</div>\n</div>\n<div class=\"resources-item\"><a class=\"resources-item-link\" rel=\"noopener noreferrer\" href=\"https://bluewallet.io/\" target=\"_blank\">Blue Wallet</a>\n<div class=\"resources-item-description\">A simple Bitcoin Wallet with Lightning support. iOS, Android, Desktop.</div>\n</div>\n</div>', 'Articles & Guides ');

